function getFactors(num) {
  let factors = [];
  for (let i = 1; i <= Math.sqrt(num); i++) {
    if (num % i === 0) {
      factors.push(i);
      if (i !== num / i) {
        factors.push(num / i);
      }
    }
  }
  return factors.sort((a, b) => a - b);
}

function isAntiprime(num) {
  let factorCount = getFactors(num).length;
  for (let i = 1; i < num; i++) {
    if (getFactors(i).length > factorCount) {
      return false;
    }
  }
  return true;
}

let antiprimes = [];
let num = 1;

while (antiprimes.length < 20) {
  if (isAntiprime(num)) {
    antiprimes.push(num);
  }
  num++;
}

console.log("The first 20 anti-primes are:");
console.log(antiprimes);
