function calculateFactorCounts(limit) {
  const factorCounts = {};
  for (let i = 1; i <= limit; i++) {
    factorCounts[i] = getFactors(i).length;
  }
  return factorCounts;
}

function isAntiprime(num, factorCounts) {
  if (!factorCounts[num]) {
    factorCounts[num] = getFactors(num).length;
  }
  for (let i = 1; i < num; i++) {
    if (!factorCounts[i]) {
      factorCounts[i] = getFactors(i).length;
    }
    if (factorCounts[i] > factorCounts[num]) {
      return false;
    }
  }
  return true;
}

