        .post('/api/users/login')
        .send({ email: 'john@example.com', password: '123456' })
        .end((err, res) => {
          res.should.have.status(200);
          res.body.should.be.a('object');
          res.body.should.have.property('token');
          done();
        });
    });
  });

  describe('GET /api/users/profile', () => {
    let token;

    before((done) => {
      chai.request(app)
